import { Component, Input } from '@angular/core';
import movieShowTimingsData from '../../assets/data/HeaderInfo.json';
import { MovieShowTiming, MovieShowTimingsData } from '../Myinterfaces/MovieShowTiming';

@Component({
  selector: 'app-movie-screens',
  templateUrl: './movie-screens.component.html',
  styleUrls: ['./movie-screens.component.css']
})
export class MovieScreensComponent {

  movieScreens: number[] = [1, 2, 3, 4];
  selectedMovieScreen: number | null = null;
  selectedMovieScreenData: MovieShowTiming[] = [];
  @Input() movieShowTimingsData: MovieShowTimingsData = {};
  @Input() showTimings!: any[];

  onMovieScreenSelect(movieScreen: number) {
    this.selectedMovieScreen = movieScreen;
    const movieScreenData: any = this.movieShowTimingsData;
    this.selectedMovieScreenData = movieScreenData[movieScreen.toString()] || [];
  }
}
<div class="container">
  <div class="screen-selection">
    <h2>Select a Movie Screen:</h2>
    <div class="button-group">
      <button
        mat-raised-button
        color="primary"
        *ngFor="let screen of movieScreens"
        (click)="onMovieScreenSelect(screen)"
      >
        Room {{ screen }}
      </button>
    </div>
  </div>

  <div class="movie-timings" *ngIf="selectedMovieScreen !== null">
    <h2>
      List of Movies and Show Timings for Movie Screen
      {{ selectedMovieScreen }}:
    </h2>
    <h3>Movie Screen {{ selectedMovieScreen }} Show Timings</h3>
    <table mat-table [dataSource]="selectedMovieScreenData">
      <ng-container matColumnDef="movie">
        <th mat-header-cell *matHeaderCellDef>Movie</th>
        <td mat-cell *matCellDef="let movieShowTiming">
          {{ movieShowTiming.movie }}
        </td>
      </ng-container>

      <ng-container matColumnDef="showTiming">
        <th mat-header-cell *matHeaderCellDef>Show Timing</th>
        <td mat-cell *matCellDef="let movieShowTiming">
          {{ movieShowTiming.showTiming }}
        </td>
      </ng-container>

      <tr mat-header-row *matHeaderRowDef="['movie', 'showTiming']"></tr>
      <tr mat-row *matRowDef="let row; columns: ['movie', 'showTiming']"></tr>
    </table>
  </div>
</div>
