import { Component } from '@angular/core';
import PorgramDetails from '../assets/data/HeaderInfo.json'
import { ProgramDataInterface } from './Myinterfaces/ProgramDetailsinterface';
import { Movie } from './Myinterfaces/MovieShowing';
import {  MovieShowTimingsData } from './Myinterfaces/MovieShowTiming';
import movieShowTimingsData from '../assets/data/HeaderInfo.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Assignment4';


  externalProgramData: ProgramDataInterface = PorgramDetails.ProgramData
  moviesData: Movie[] = PorgramDetails.MoviesShowing;
  moviesData2: Movie[] = PorgramDetails.MoviesShowing;
  //movieShowTimingsData: MovieShowTimingsData = PorgramDetails;
  selectedMovieScreen: number | null = null;
  movieShowTimingsData: MovieShowTimingsData = movieShowTimingsData as MovieShowTimingsData;

}


<router-outlet>
<header>

  <app-header></app-header>

</header>
<footer>
  <app-footer></app-footer>


</footer>

<app-list-of-movies></app-list-of-movies>
<app-movie-screens></app-movie-screens>
</router-outlet>


<mat-tab-group>
  <mat-tab label="List of Movies">
    <app-list-of-movies [externalMovieInfo1]="moviesData2"></app-list-of-movies>


    <mat-tab label="Movie Screens">
      <app-movie-screens [movieShowTimingsData]="movieShowTimingsData"></app-movie-screens>
    </mat-tab>
  </mat-tab>
