import { Component, Input } from '@angular/core';
import PorgramDetails from '../../assets/data/HeaderInfo.json'
import { ProgramDataInterface } from '../Myinterfaces/ProgramDetailsinterface';
import { MovieInterface } from '../Myinterfaces/MovieDetailsInterface';
@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
//  @Input() headerinfo!: headerinfo; 
externalProgramData: ProgramDataInterface = PorgramDetails.ProgramData
externalMovieData: MovieInterface = PorgramDetails.MovieName

}
<!-- app.component.html -->
<header class="header-section"> 
  <h1>{{ externalProgramData.programName }}</h1>
  <p class="program-info">{{ externalProgramData.programCode }}</p>
  <p class="program-info">{{ externalProgramData.schedule }}</p>

  <h2>{{ externalMovieData.theatreName }}</h2>
  <p class="theatre-info">Location: {{ externalMovieData.location }}</p>
  <p class="theatre-info">Contact Number: {{ externalMovieData.contactNumber }}</p>
</header>
