import { Component } from '@angular/core';
import movieShowTimingsData from '../../assets/data/HeaderInfo.json';
import { MovieShowTiming, MovieShowTimingsData } from '../Myinterfaces/MovieShowTiming';

@Component({
  selector: 'app-movie-screens',
  templateUrl: './movie-screens.component.html',
  styleUrls: ['./movie-screens.component.css']
})
export class MovieScreensComponent {

  movieScreens: number[] = [1, 2, 3, 4];
  selectedMovieScreen: number | null = null;
  selectedMovieScreenData: MovieShowTiming[] = [];

  onMovieScreenSelect(movieScreen: number) {
    this.selectedMovieScreen = movieScreen;
    const movieScreenData: any = movieShowTimingsData;
    this.selectedMovieScreenData = movieScreenData[movieScreen.toString()] || [];
  }
}
<p>movie-screens works!</p>
<h3>Select a Movie Screen:</h3>
<div *ngFor="let screen of movieScreens">
  <button mat-raised-button (click)="onMovieScreenSelect(screen)">
    Room {{ screen }}
  </button>
</div>

<div *ngIf="selectedMovieScreen">
  <h3>List of Movies and Show Timings for Movie Screen {{ selectedMovieScreen }}:</h3>
  <!-- Add mat-table here to display the list of movies and show timings -->
</div>



<div>
    <h2>Select a Movie Screen (Room)</h2>
    <button *ngFor="let movieScreen of movieScreens" (click)="onMovieScreenSelect(movieScreen)">
      Movie Screen {{ movieScreen }}
    </button>
  </div>
