import { Component } from '@angular/core';
import programDetails from '../assets/data/HeaderInfo.json';
import { ProgramDataInterface } from './Myinterfaces/ProgramDetailsinterface';
import { Movie } from './Myinterfaces/MovieShowing';
import { MovieShowTimingsData } from './Myinterfaces/MovieShowTiming';
import headInfo from '../assets/data/HeaderInfo.json';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Assignment4';

  externalProgramData: ProgramDataInterface = programDetails.ProgramData;
  moviesData2: Movie[] = programDetails.MoviesShowing;
  selectedMovieScreen: number | null = null;
 // movieShowTimingsData: MovieShowTimingsData = programDetails.MovieShowTimingsData;
 // movieShowTimingsData: MovieShowTimingsData = movieShowTimingsData.MovieShowTimingsData;
 // movieShowTimingsData: MovieShowTimingsData = programDetails.MovieShowTimingsData;
  movieShowTimingsData = headInfo.MovieShowTimingsData;
  movieScreensData = headInfo.MovieShowTimingsData;

}
<!-- app.component.html -->
<div class="container">
  <div class="screen-selection">
    <h2>Select a Movie Screen:</h2>
    <div *ngFor="let screen of movieScreens">
      <button mat-raised-button color="primary" (click)="onMovieScreenSelect(screen)">
        Room {{ screen }}
      </button>
    </div>
  </div>

  <div class="movie-timings" *ngIf="selectedMovieScreen !== null">
    <h2>List of Movies and Show Timings for Movie Screen {{ selectedMovieScreen }}:</h2>
    <h3>Movie Screen {{ selectedMovieScreen }} Show Timings</h3>
    <table mat-table [dataSource]="selectedMovieScreenData">

      <!-- Movie Column -->
      <ng-container matColumnDef="movie">
        <th mat-header-cell *matHeaderCellDef> Movie </th>
        <td mat-cell *matCellDef="let movieShowTiming"> {{ movieShowTiming.movie }} </td>
      </ng-container>

      <!-- Show Timing Column -->
      <ng-container matColumnDef="showTiming">
        <th mat-header-cell *matHeaderCellDef> Show Timing </th>
        <td mat-cell *matCellDef="let movieShowTiming"> {{ movieShowTiming.showTiming }} </td>
      </ng-container>

      <!-- Table Rows -->
      <tr mat-header-row *matHeaderRowDef="['movie', 'showTiming']"></tr>
      <tr mat-row *matRowDef="let row; columns: ['movie', 'showTiming']"></tr>
    </table>
  </div>
</div>
